// src/pages/Menu.jsx
import React, { useState } from "react";
import foodItems from "../data/foodItems";
import FoodCard from "../components/FoodCard";

const categories = ["All", "Main Course", "Snacks", "Desserts", "Beverages"];

export default function Menu() {
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [searchTerm, setSearchTerm] = useState("");

  // Filtered list based on category and search input
  const filteredItems = foodItems.filter((item) => {
    const matchesCategory =
      selectedCategory === "All" || item.category === selectedCategory;
    const matchesSearch = item.name
      .toLowerCase()
      .includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  return (
    <div className="max-w-6xl mx-auto py-10 px-4">
      <h1 className="text-3xl font-bold text-orange-600 text-center mb-6">
        Explore Our Menu
      </h1>

      {/* Search Input */}
      <div className="flex justify-center mb-6">
        <input
          type="text"
          placeholder="Search for a dish..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full max-w-md px-4 py-2 border border-gray-300 rounded-full shadow-sm focus:outline-none focus:ring-2 focus:ring-orange-400"
        />
      </div>

      {/* Category Filters */}
      <div className="flex flex-wrap justify-center gap-4 mb-8">
        {categories.map((category) => (
          <button
            key={category}
            onClick={() => setSelectedCategory(category)}
            className={`px-4 py-2 rounded-full text-sm font-medium border transition ${
              selectedCategory === category
                ? "bg-orange-500 text-white border-orange-500"
                : "bg-white text-orange-500 border-orange-500 hover:bg-orange-50"
            }`}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Food Cards */}
      {filteredItems.length > 0 ? (
        <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {filteredItems.map((item) => (
            <FoodCard key={item.id} item={item} />
          ))}
        </div>
      ) : (
        <p className="text-center text-gray-500">No matching items found.</p>
      )}
    </div>
  );
}
