// src/pages/Menu.jsx
import React from "react";
import foodItems from "../data/foodItems"; // Ensure this file exists
import { useCart } from "../context/CartContext";

export default function Menu() {
  const { addToCart } = useCart();

  return (
    <div className="min-h-screen bg-gray-50 py-10 px-4">
      <h1 className="text-4xl font-bold text-center text-orange-600 mb-10">
        Explore Our Menu
      </h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8 max-w-7xl mx-auto">
        {foodItems.map((item) => (
          <div
            key={item.id}
            className="bg-white rounded-lg shadow p-4 flex flex-col items-center text-center hover:shadow-md transition"
          >
            {/* Placeholder image */}
            <img
              src={item.image} // Make sure images exist in public/assets
              alt={item.name}
              className="w-full h-40 object-cover rounded mb-4"
            />
            <h2 className="text-xl font-semibold text-gray-800 mb-1">
              {item.name}
            </h2>
            <p className="text-gray-600 text-sm mb-2">{item.description}</p>
            <p className="text-lg font-bold text-teal-600 mb-4">
              â‚¹{item.price}
            </p>
            <button
              onClick={() => addToCart(item)}
              className="bg-teal-500 text-white px-4 py-2 rounded hover:bg-teal-600 transition"
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
