import React, { useState } from "react";
import { useCart } from "../context/CartContext";
import { useNavigate } from "react-router-dom";

const DELIVERY_FEE = 30;
const GST_RATE = 0.12;

const Checkout = () => {
  const { cart, clearCart } = useCart();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    fullName: "",
    phone: "",
    flat: "",
    area: "",
    landmark: "",
    pincode: "",
    city: "",
    state: "",
    paymentMethod: "Cash on Delivery",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { fullName, phone, flat, area, pincode, city, state } = form;

    if (fullName && phone && flat && area && pincode && city && state) {
      clearCart();
      navigate("/order-success");
    } else {
      alert("Please fill in all required fields.");
    }
  };

  const subtotal = cart.reduce(
    (total, item) => total + item.price * item.quantity,
    0
  );
  const gst = subtotal * GST_RATE;
  const total = subtotal + gst + DELIVERY_FEE;

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h2 className="text-2xl font-bold mb-6">Checkout</h2>
      <form
        onSubmit={handleSubmit}
        className="bg-white shadow p-6 rounded space-y-4"
      >
        <div>
          <label className="block font-medium mb-1">Full Name *</label>
          <input
            type="text"
            name="fullName"
            value={form.fullName}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
            required
          />
        </div>
        <div>
          <label className="block font-medium mb-1">Phone Number *</label>
          <input
            type="tel"
            name="phone"
            value={form.phone}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
            required
          />
        </div>
        <div>
          <label className="block font-medium mb-1">
            Flat, House no., Building, Company, Apartment *
          </label>
          <input
            type="text"
            name="flat"
            value={form.flat}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
            required
          />
        </div>
        <div>
          <label className="block font-medium mb-1">
            Area, Street, Sector, Village *
          </label>
          <input
            type="text"
            name="area"
            value={form.area}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
            required
          />
        </div>
        <div>
          <label className="block font-medium mb-1">Landmark</label>
          <input
            type="text"
            name="landmark"
            value={form.landmark}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
          />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block font-medium mb-1">Pincode *</label>
            <input
              type="text"
              name="pincode"
              value={form.pincode}
              onChange={handleChange}
              className="w-full border px-3 py-2 rounded"
              required
            />
          </div>
          <div>
            <label className="block font-medium mb-1">Town/City *</label>
            <input
              type="text"
              name="city"
              value={form.city}
              onChange={handleChange}
              className="w-full border px-3 py-2 rounded"
              required
            />
          </div>
          <div>
            <label className="block font-medium mb-1">State *</label>
            <input
              type="text"
              name="state"
              value={form.state}
              onChange={handleChange}
              className="w-full border px-3 py-2 rounded"
              required
            />
          </div>
        </div>
        <div>
          <label className="block font-medium mb-1">Payment Method</label>
          <select
            name="paymentMethod"
            value={form.paymentMethod}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded"
          >
            <option>Cash on Delivery</option>
            <option>UPI</option>
            <option>Card</option>
          </select>
        </div>
        <div className="border-t pt-4 text-right text-gray-700">
          <p>Subtotal: ₹{subtotal.toFixed(2)}</p>
          <p>GST (12%): ₹{gst.toFixed(2)}</p>
          <p>Delivery Fee: ₹{DELIVERY_FEE.toFixed(2)}</p>
          <h3 className="text-xl font-bold text-black">
            Total: ₹{total.toFixed(2)}
          </h3>
        </div>
        <button
          type="submit"
          className="mt-4 bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700 w-full"
        >
          Place Order
        </button>
      </form>
    </div>
  );
};

export default Checkout;
